	//Instance variables. 
	protected String Name;
	protected String Description;
	protected int[] workingArray;
	
	
	/**
	 * Runs the sort a specified number of times and returns the average execution time 
	 * @param array Array to sort
	 * @param count Number of times to run the test
	 * @return Average execution time in ms
	 */
	public double TestSort(int[] array, int count)
	{
		double[] results=new double[count+20];
		double r = 0.00;

		
		for(int i = 0; i<count+20;i++)
		{
			long startTime = System.nanoTime();
			sort(array);
			long endTime = System.nanoTime();		
			results[i] = ((double)(endTime - startTime))/1000000;

		}
		

		for (int i = 20;i<count+20;i++)
			r += results[i];
		return r/count;
	}
	
	/**
	 * Gets the name of the Sort
	 * @return Value of String Name
	 */
	public String getName() {
		return this.Name;
	}
	
	public String getDescription(){
		return this.Description;
	}
	
	/**
	 * Generic sorting algorithm
	 * @param array
	 * @return
	 */
	public abstract int[] sort(int[] array);
	protected void swap(int idx1,int idx2)
	{
		int tmp = workingArray[idx1];
		workingArray[idx1] = workingArray[idx2];
		workingArray[idx2] = tmp;
	}
